const StickyNoteTransition = {
  initial: {
    y: "-19.3rem",
    // y: "0 .3rem",
    x: "0.3rem"
  }, animate: {
    y: "-7rem",
    transition: {
      duration: 1.5,
      type: "tween"
    }
  }, exit: {
    y: "0",
    transition: {
      duration: 1,
      type: "tween"
    }
  }
}
export default function SpeakersSection() {
  const [Speaker, setSpeaker] = React.useState(0)
  const swiperRef = useRef(null);
  const [isHoverredOn, setIsHoveredOn] = React.useState(0)
  const setStyle = () => {
    document.getElementById("Speaker-info").style = ` transform: translate(0.3rem,-15.3rem) rotateZ(${DEG[swiperRef.current.snapIndex]}deg) !important;`
  }
  const setSpeakerInfos = (id) => {
    setSpeaker(Speakers[id])
  }
  return (
    <div id="SpeakersSlider" className="container mx-auto w-3/5  h-screen  flex justify-start	 flex-col gap-8 pt-9">
      <Casque />
      <Compas />
      <Usb />
      <Arrow />

      {Speakers != null ? <>
        <div id="title"
          className="w-full bg-no-repeat bg-contain	bg-center	bg-origin-padding flex items-center justify-center max-h-40	h-16"
        >
          <h1
            className="text-2xl text-center font-bold"
            style={{ color: "#101FA3", }}
          >Speakers
          </h1>
        </div>
        <div id="SliderDots"
          className="flex items-center justify-center gap-6 w-full max-h-20 h-fit"
        >
          {Speakers.map((person) => (
            <>
              {swiperRef.current == null || swiperRef.current.snapIndex === Speakers.indexOf(person) ? <>
                <ActiveDot />

              </> : <>
                <EmptyDot />

              </>}

            </>
          ))}


        </div>

        <div className="flex justify-between items-center h-fit">

          <PervButton Perv={() => {
            swiperRef.current?.slidePrev();
            setSpeakerInfos(swiperRef.current.snapIndex)
          }}></PervButton>

          <Swiper
            effect={"cards"}
            modules={[EffectCards, Navigation]}
            className="mySwiper"
            onBeforeInit={(swiper) => {
              swiperRef.current = swiper;
              setSpeakerInfos(0)
            }}
            navigation
            onSlideChange={() => setStyle()}
          >
            {Speakers.map((person) => (
              <SwiperSlide  >
                <div id="Speaker-img" className=" h-3/4 m-4 over overflow-hidden"
                  onMouseEnter={() => { setIsHoveredOn(1) }}
                  onMouseLeave={() => { setIsHoveredOn(0) }}
                >
                  <img className="object-cover" src={Pic} alt="" />
                </div>

              </SwiperSlide>
            ))}
            <AnimatePresence>
              <motion.div
                id="Speaker-info"
                className="slide"
                variants={StickyNoteTransition}
                initial="initial"
                animate={isHoverredOn === 1 ? "animate" : "null"}
                exit={isHoverredOn === 1 ? "exit" : "null"}
              >
                {Speaker != null ? <>
                  <span className="text-left text-base"
                    style={{ color: "#542E30" }}>
                    {Speaker.name}
                  </span>
                  <span className="text-left text-base"
                    style={{ color: "#542E30" }}
                  >
                    {Speaker.occupation}

                  </span>
                  <span
                    className="text-base text-left  "
                    style={{ color: "#AB6468" }}>
                    {Speaker.description}
                  </span>

                </> : <></>}
              </motion.div>
            </AnimatePresence>




          </Swiper>
          <NextButton Next={() => {
            swiperRef.current?.slideNext()
            setSpeakerInfos(swiperRef.current.snapIndex)

          }}></NextButton>
        </div>
      </> : <></>}


    </div>

  );
}
